<template>
	<view>
    <!-- 输入框 -->
		<view>
			<text> {{content.content}}</text>
		</view>
    <!-- 图片组 -->
		<view v-for="(item,key) in content.images" :key="key">
			<image :src="item.content" :mode="!item.mode ? 'scaleToFill' : item.mode "></image>
		</view>
		<view v-if="content.attachs && content.attachs.length > 0">
			<view>附件:</view>
			<view v-for="(item,key) in content.attachs" :key="key" @click="downloadAttach(item.attachUrl)">{{item.attachName}}</view>
		</view>
  </view>
</template>

<script>
  import {uniIcon} from '@dcloudio/uni-ui'

  export default {
		name: 'editor',
		components:{uniIcon},
		data () {
			return {
				// contentA: ''
			}
		},
    props:{
      content: {
				type: [Object, Array],
				default () {
					return {
						content: '',
						images: []
					}
				}
			}
    },
		methods:{
			downloadAttach (url) {
				this.$kwz.downloadAttach(url,(path) => {
					this.$kwz.alert(`文件下载成功，已保存至${path}中`)
				}, this)
			}
		}
  }
</script>

